Muffin.drawPop = function(arrhey)
{
	var e = arrhey;

	var unpop = function()
	{
		elt = e.pop();
		if (elt != undefined)
		{
			elt = $(elt).get(0);
			$(elt).addClass("complete");
			setTimeout(unpop, 100);
		}
	}
	setTimeout(unpop, 400);
}

Muffin.escapeLeft = function(arrhey, callback)
{
	var e = arrhey;

	var unpop = function()
	{
		elt = e.pop();
		if (elt != undefined)
		{
			elt = $(elt).get(0);
			$(elt).addClass("escape-left");
			setTimeout(unpop, 100);
		}
		else
		{
			callback();
		}
	}
	unpop();
}

